import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../client';

const CrewmateInfo = ({ data }) => {
  
  const [crewmate, setCrewmate] = useState([]);

  useEffect(() => {
    const fetchCrewmate = async () => {
        const { data } = await supabase
            .from('Crewmates')
            .select()
            .order('created_at', { ascending: true });

        // set state of crewmates
        setCrewmate(data);
    };

    fetchCrewmate();
}, []);

  // If crewmate is not found, display a message
  if (!crewmate) {
    return <div>Crewmate not found.</div>;
  }

  return (
    <div>
      <h2>{crewmate.title}</h2>
      <p>Author: {crewmate.author}</p>
      <p>Description: {crewmate.description}</p>
    </div>
  );
};

export default CrewmateInfo;
