import React from 'react';
import { useParams } from 'react-router-dom';

const CrewmateInfo = ({ data }) => {
  // Get the crewmate ID from the URL
  const { id } = useParams();

  // Find the crewmate with the matching ID
  const crewmate = data.find(crewmate => crewmate.id === id);

  // If crewmate is not found, display a message
  if (!crewmate) {
    return <div>Crewmate not found.</div>;
  }

  return (
    <div>
      <h2>{crewmate.title}</h2>
      <p>Author: {crewmate.author}</p>
      <p>Description: {crewmate.description}</p>
    </div>
  );
};

export default CrewmateInfo;
